    onBtnWithdrawClick: function(button, e, eOpts) {
        var insObj={
            AccountNo:Ext.ComponentQuery.query('#hdnAccountNo')[0].getValue(),
            WithdrawDate:Ext.ComponentQuery.query('#txtWithdrawDateAD')[0].getValue(),
            WithdrawAmount:Ext.ComponentQuery.query('#txtPaidAmount')[0].getValue(),
            ChequeNo:Ext.ComponentQuery.query('#txtChequeNo')[0].getValue(),
            PayeeName:Ext.ComponentQuery.query('#txtPayeeName')[0].getValue(),
            ContraAccount:Ext.ComponentQuery.query('#ddlContraAccount')[0].getValue(),
            WithdrawType:'N',
            MaxWithdrawAmount:Ext.ComponentQuery.query('#txtWithdrawAmountLimit')[0].getValue(),
            TranOfficeCode:getOfficeCode(),
            AcOfficeCode:Ext.ComponentQuery.query('#ddlOffice')[0].getValue(),
            ValueDate:Ext.ComponentQuery.query('#txtValueDateAD')[0].getValue(),
            ChargeAmount:Ext.ComponentQuery.query('#txtChargeAmount')[0].getValue(),
            ChargeType:Ext.ComponentQuery.query('#ddlChargeType')[0].getValue(),
            ChargeSource:Ext.ComponentQuery.query('#ddlChargeSource')[0].getValue(),
            User1:getCurrentUser(),
            InsertUpdate:'I'
        };
        var waitSave = watiMsg("Withdrawing...Please wait ...");
        Ext.Ajax.request({
            url: '../Handlers/Finance/Transaction/EditTransaction/PublicSavingWithdrawHandler.ashx',
            params: {
                method:'SavePublicSavingWithdraw',
                publicSavingWithdraw:JSON.stringify(insObj)
            },
            success: function ( response, request ) {
                waitSave.hide();
                var out=Ext.decode(response.responseText);
                console.log(out);

                if(out.success==="true")
                {
                    msg("SUCCESS",out.message);
                }
                else
                {
                    msg("FAILURE",out.message);
                }
            },
            failure: function ( result, request ) {
                waitSave.hide();
                var out=Ext.decode(response.responseText);
                msg("FAILURE",out.message);
            }



        });

    },

