Ext.define('MyApp.view.SelectGroupCenterPopup', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.form.Panel',
        'Ext.form.field.Text',
        'Ext.button.Button',
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.selection.RowModel'
    ],

    height: 500,
    itemId: 'SelectGroupCenterPopup',
    width: 700,
    autoScroll: true,
    resizable: false,
    layout: 'fit',
    title: 'Select Center',
    maximizable: true,
    minimizable: true,
    modal: true,
    y: 80,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    frame: true,
                    autoScroll: true,
                    bodyPadding: 10,
                    title: '',
                    items: [
                        {
                            xtype: 'container',
                            layout: 'table',
                            items: [
                                {
                                    xtype: 'textfield',
                                    itemId: 'txtSearchCenterOfficeCode',
                                    fieldLabel: 'Office Code'
                                },
                                {
                                    xtype: 'textfield',
                                    itemId: 'txtSearchCenterName',
                                    fieldLabel: 'Center Name'
                                },
                                {
                                    xtype: 'button',
                                    colspan: 2,
                                    itemId: 'btnSearchCenter',
                                    margin: '0 0 0 5',
                                    text: 'Search',
                                    listeners: {
                                        click: {
                                            fn: me.onBtnSearchCenterClick,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            xtype: 'gridpanel',
                            itemId: 'grdSearchCenter',
                            title: '',
                            store: 'GroupCenterSearchStore',
                            columns: [
                                {
                                    xtype: 'gridcolumn',
                                    dataIndex: 'CenterCode',
                                    text: 'Center Code'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 161,
                                    dataIndex: 'CenterName',
                                    text: 'Center Name'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    dataIndex: 'EmployeeId',
                                    text: 'Employee Id'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 201,
                                    dataIndex: 'EmployeeName',
                                    text: 'Employee Name'
                                }
                            ],
                            selModel: Ext.create('Ext.selection.RowModel', {
                                listeners: {
                                    beforeselect: {
                                        fn: me.onRowModelBeforeSelect,
                                        scope: me
                                    }
                                }
                            })
                        }
                    ]
                }
            ],
            listeners: {
                afterrender: {
                    fn: me.onSelectGroupCenterPopupAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onBtnSearchCenterClick: function(button, e, eOpts) {

        var officeCode=Ext.ComponentQuery.query('#txtSearchCenterOfficeCode')[0];
        var centerName=Ext.ComponentQuery.query('#txtSearchCenterName')[0];
        var centerobj={

            OfficeCode :officeCode.getValue(),
            CenterName: centerName.getValue()

        };
        var waitSave = watiMsg('Loading Data. Please wait ...');
        Ext.Ajax.request({
            url: '../Handlers/GeneralMasterParameters/Center/CenterHandler.ashx',
            params: {
                method:'Search', center:JSON.stringify(centerobj)
            },
            success: function(response){


            var obj = Ext.decode(response.responseText);


              if(obj.success === "true")
              {

                  var store=Ext.getStore('GroupCenterSearchStore');
                  store.removeAll();
                  store.add(obj.root);

              }
                else
                {

                    msg("FAILURE",obj.message);
                }


            waitSave.hide();



            }
        });
    },

    onRowModelBeforeSelect: function(rowmodel, record, index, eOpts) {
        Ext.MessageBox.confirm('Select', 'Are you sure ?', function(btn){

           if(btn === 'yes'){


               Ext.ComponentQuery.query('#txtCenterCode')[0].setValue(record.data.CenterCode);
               Ext.ComponentQuery.query('#txtCenterName')[0].setValue(record.data.CenterName);


              // return true;
           }
           else
           {

           }
            var v=Ext.ComponentQuery.query('#SelectGroupCenterPopup')[0];

            v.close();
        });

    },

    onSelectGroupCenterPopupAfterRender: function(component, eOpts) {
        var store=Ext.getStore('GroupCenterSearchStore');
        store.removeAll();
    }

});