Ext.define('MyApp.view.SelectLoanProductsPopup', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.form.field.Text',
        'Ext.button.Button',
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.selection.RowModel',
        'Ext.grid.column.Column'
    ],

    height: 500,
    itemId: 'SelectLoanProductsPopup',
    width: 700,
    autoScroll: true,
    layout: 'fit',
    title: 'Select Loan Products',
    maximizable: true,
    minimizable: true,
    modal: true,
    y: 80,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'container',
                    items: [
                        {
                            xtype: 'container',
                            layout: 'table',
                            items: [
                                {
                                    xtype: 'textfield',
                                    itemId: 'txtSearchLoanProduct',
                                    fieldLabel: 'Search'
                                },
                                {
                                    xtype: 'button',
                                    itemId: 'btnSearchProduct',
                                    text: 'Search',
                                    listeners: {
                                        click: {
                                            fn: me.onBtnSearchProductClick,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            xtype: 'gridpanel',
                            itemId: 'grdSearchLoanProduct',
                            title: '',
                            store: 'ProductTypeStore',
                            selModel: Ext.create('Ext.selection.RowModel', {
                                listeners: {
                                    beforeselect: {
                                        fn: me.onRowModelBeforeSelect,
                                        scope: me
                                    }
                                }
                            }),
                            columns: [
                                {
                                    xtype: 'gridcolumn',
                                    dataIndex: 'ProductCode',
                                    text: 'Product Code'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 323,
                                    dataIndex: 'ProductName',
                                    text: 'Product Name'
                                }
                            ]
                        }
                    ]
                }
            ],
            listeners: {
                afterrender: {
                    fn: me.onSelectLoanProductsPopupAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onBtnSearchProductClick: function(button, e, eOpts) {
        var waitSave = watiMsg('Loading Data. Please wait ...');
        Ext.Ajax.request({
            url:'../Handlers/Finance/Transaction/EditTransaction/SavingTransferProcHandler.ashx',
            params:{method:'GetSavTransferProc',
                    ProductName: Ext.ComponentQuery.query('#txtSearchLoanProduct')[0].getValue()
                   },
            success: function ( result, request ) {

                var obj = Ext.decode(result.responseText);
                if(obj.success === 'true'){
                    //console.log("Resu",obj.root);
                    var store =Ext.getStore('ProductTypeStore');
                    store.removeAll();
                    store.add(obj.root);


                }else{msg('FAILURE','Could Not Load Data');}
                waitSave.hide();
            },
            failure: function(form, action) {
                msg("FAILURE","Could Not Load Data");  waitSave.hide();  }
        });
    },

    onRowModelBeforeSelect: function(rowmodel, record, index, eOpts) {
        Ext.MessageBox.confirm('Select', 'Are you sure ?', function(btn){

           if(btn === 'yes'){
               console.log('record',record);


              Ext.ComponentQuery.query('#txtSavingTypeToCode')[0].setValue(record.data.ProductCode);
              Ext.ComponentQuery.query('#txtSavingTypeToDesc')[0].setValue(record.data.ProductName);


           }
           else
           {

           }
            var v=Ext.ComponentQuery.query('#SelectLoanProductsPopup')[0];

            v.close();
        });

    },

    onSelectLoanProductsPopupAfterRender: function(component, eOpts) {
        Ext.Ajax.request({
            url:'../Handlers/Finance/Transaction/EditTransaction/SavingTransferProcHandler.ashx',
            params:{method:'GetSavTransferProc',
                    ProductName: null
                   },
            success: function ( result, request ) {

                var obj = Ext.decode(result.responseText);
                if(obj.success === 'true'){
                    //console.log("Resu",obj.root);
                    var store =Ext.getStore('ProductTypeStore');
                    store.removeAll();
                    store.add(obj.root);


                }else{msg('FAILURE','Could Not Load Data');}
            },
            failure: function(form, action) {
                msg("FAILURE","Could Not Load Data");    }
        });
    }

});