Ext.define('MyApp.view.SelectVdcPopup', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.form.Panel',
        'Ext.form.field.Text',
        'Ext.button.Button',
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.selection.RowModel',
        'Ext.toolbar.Paging'
    ],

    height: 500,
    itemId: 'SelectVdcPopup',
    width: 700,
    autoScroll: true,
    title: 'Select VDC',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    frame: true,
                    itemId: 'frmSelectVdcPopup',
                    bodyPadding: 10,
                    items: [
                        {
                            xtype: 'container',
                            layout: 'table',
                            items: [
                                {
                                    xtype: 'textfield',
                                    itemId: 'txtSearchVdc',
                                    fieldLabel: 'Search'
                                },
                                {
                                    xtype: 'button',
                                    colspan: 2,
                                    itemId: 'btnSearchVdc',
                                    margin: '0 0 0 5',
                                    text: 'Search',
                                    listeners: {
                                        click: {
                                            fn: me.onBtnSearchVdcClick,
                                            scope: me
                                        }
                                    }
                                }
                            ]
                        },
                        {
                            xtype: 'gridpanel',
                            itemId: 'grdSearchVDC',
                            title: '',
                            store: 'VdcSearchStore',
                            columns: [
                                {
                                    xtype: 'gridcolumn',
                                    dataIndex: 'VDCNPCode',
                                    text: 'VDC NP Code'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 206,
                                    dataIndex: 'VDCNPDesc',
                                    text: 'VDC NP Desc'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    dataIndex: 'DistrictCode',
                                    text: 'District Code'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 205,
                                    dataIndex: 'DistrictDesc',
                                    text: 'District Description'
                                }
                            ],
                            selModel: Ext.create('Ext.selection.RowModel', {
                                listeners: {
                                    beforeselect: {
                                        fn: me.onRowModelBeforeSelect,
                                        scope: me
                                    }
                                }
                            })
                        }
                    ],
                    dockedItems: [
                        {
                            xtype: 'pagingtoolbar',
                            dock: 'bottom',
                            itemId: 'pgsliderSearchVDC',
                            width: 360,
                            displayInfo: true
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onBtnSearchVdcClick: function(button, e, eOpts) {
        var vdcName=Ext.ComponentQuery.query('#txtSearchVdc')[0];
        var waitSave = watiMsg('Loading Data. Please wait ...');
        Ext.Ajax.request({
            url: '../Handlers/Common/VdcHandler.ashx',
            params: {
                method:'SearchVdc',
               // districtCode:districtCode.getValue(),
                vdcName: vdcName.getValue()
            },
            success: function(response){


                var obj = Ext.decode(response.responseText);


                  if(obj.success === "true")
                  {

                      var store=Ext.getStore('VdcSearchStore');
                      store.removeAll();
                      store.add(obj.root);

                  }
                else
                {

                    msg("FAILURE",obj.message);
                }

                waitSave.hide();
            }
        });
    },

    onRowModelBeforeSelect: function(rowmodel, record, index, eOpts) {
        Ext.MessageBox.confirm('Select', 'Are you sure ?', function(btn){

           if(btn === 'yes'){
               console.log('record',record);

               Ext.ComponentQuery.query('#txtVdcNpCode')[0].setValue(record.data.VDCNPCode);
               Ext.ComponentQuery.query('#txtVdcNpName')[0].setValue(record.data.VDCNPDesc);


              // return true;
           }
           else
           {

           }
            var v=Ext.ComponentQuery.query('#SelectVdcPopup')[0];

            v.close();
        });

    }

});