Ext.define('MyApp.view.SelectMemberAccountForCheque', {
    extend: 'Ext.window.Window',

    requires: [
        'Ext.button.Button',
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.grid.column.Column',
        'Ext.selection.RowModel'
    ],

    height: 500,
    itemId: 'SelectMemberAccountForCheque',
    width: 700,
    autoScroll: true,
    resizable: false,
    title: 'Member Account For Cheque',
    maximizable: true,
    minimizable: true,
    modal: true,
    y: 80,

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'container',
                    items: [
                        {
                            xtype: 'button',
                            itemId: 'btnMemberAcForCheque',
                            padding: 5,
                            text: 'Search',
                            listeners: {
                                click: {
                                    fn: me.onBtnMemberAcForChequeClick,
                                    scope: me
                                }
                            }
                        }
                    ]
                },
                {
                    xtype: 'container',
                    items: [
                        {
                            xtype: 'gridpanel',
                            itemId: 'grdMemberAccounts',
                            title: 'Member Accounts',
                            store: 'SelectMemberAccountForChequeStore',
                            columns: [
                                {
                                    xtype: 'gridcolumn',
                                    width: 177,
                                    dataIndex: 'AccountNo',
                                    text: 'Account No'
                                },
                                {
                                    xtype: 'gridcolumn',
                                    width: 190,
                                    dataIndex: 'SavingAccountNo',
                                    text: 'Saving Account No'
                                }
                            ],
                            selModel: Ext.create('Ext.selection.RowModel', {
                                listeners: {
                                    beforeselect: {
                                        fn: me.onRowModelBeforeSelect,
                                        scope: me
                                    }
                                }
                            })
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onBtnMemberAcForChequeClick: function(button, e, eOpts) {
        var offCode=Ext.ComponentQuery.query('#ddlOffice')[0].getValue();
        var clientNo=Ext.ComponentQuery.query('#txtMemberCode')[0].getValue();

        var store=Ext.getStore('SelectMemberAccountForChequeStore');
        store.removeAll();

        Ext.Ajax.request({
            url: '../Handlers/Finance/Transaction/SavingTransaction/ClientSavingAccountHandler.ashx',
            params: {
                method:'GetMemberAccountForCheque',
                offCode:offCode,
                clientNo:clientNo
            },
            success: function(response){

            var data=Ext.decode(response.responseText);
            store.removeAll();
            store.add(data.root);


        }
        });
    },

    onRowModelBeforeSelect: function(rowmodel, record, index, eOpts) {
        Ext.MessageBox.confirm('Select', 'Are you sure ?', function(btn){

            if(btn === 'yes'){
                console.log('record',record);

                Ext.ComponentQuery.query('#txtAccountNo')[0].setValue(record.data.AccountNo);
                //Ext.ComponentQuery.query('#txtMemberName')[0].setValue(record.data.ClientName);


                // return true;
            }
            else
            {

            }
            var v=Ext.ComponentQuery.query('#SelectMemberAccountForCheque')[0];

            v.close();
        });
    }

});