    onDdlCenterChange: function(field, newValue, oldValue, eOpts) {
        var centerCode =Ext.ComponentQuery.query('#ddlCenter')[0].getValue();
        var memberName = '';


        Ext.Ajax.request({
            url: '../Handlers/Finance/Transaction/LoanTransaction/MeLoanDisbursementHandler.ashx',
            params: {
                method:'GetMeLoanMember',
                offCode:getOfficeCode(),centerCode:centerCode,memberName:memberName
            },
            success: function(response){

                var storeMemberCode = Ext.getStore('MeLoanDisMemberCodeStore');
                storeMemberCode.removeAll();
                 var data=Ext.decode(response.responseText);
                storeMemberCode.add(data.root);


            }
        });

    },

