    onFrmMELoanDisbursementAfterRender: function(component, eOpts) {

        Ext.ComponentQuery.query('#txtDateBS')[0].setValue(getMisDateBS());
        Ext.ComponentQuery.query('#txtDateAD')[0].setValue(getMisDateAD());
        //var obj=getReferences('LOAN_INTEREST_CALC_METHOD');
        var store=Ext.getStore('CenterCodeStore');
        store.removeAll();

        Ext.Ajax.request({
            url: '../Handlers/Finance/Maintenance/CenterHandler.ashx',
            params: {
                method:'GetTransferCenterList',
                offCode:getOfficeCode()
            },
            success: function(response){

                var data=Ext.decode(response.responseText);
                store.removeAll();
                store.add(data.root);


            }
        });


        var interestCalcMethodStore=Ext.getStore('InterestCalcMethodStore');
        interestCalcMethodStore.removeAll();

        getReferences('LOAN_INTEREST_CALC_METHOD',function(data){

            interestCalcMethodStore.add(data.root);
        });

        //funding agency

        Ext.Ajax.request({
             url: '../Handlers/Finance/Transaction/LoanTransaction/FundingAgencyHandler.ashx',
            params: {
                method:'Get'
            },
            success: function(response){

                var data=Ext.decode(response.responseText);
                var storeFundingStore =Ext.getStore('FundingAgencyStore');
                storeFundingStore.removeAll();
                storeFundingStore.add(data.root);


            }
        });

        //LoanStatus

        var loanStatusStore=Ext.getStore('LoanStatusStore');
        loanStatusStore.removeAll();

        getReferences('LOAN_STATUS',function(data){

            loanStatusStore.add(data.root);
        });
        //LoanPeriodType

        var loanPeriodTypeStore=Ext.getStore('LoanPeriodTypeStore');
        loanPeriodTypeStore.removeAll();

        getReferences('LOAN_PERIOD_TYPE',function(data){

            loanPeriodTypeStore.add(data.root);
        });
        //InstallmentPeriodType

        var installmentPeriodTypeStore=Ext.getStore('InstallmentPeriodTypeStore');
        installmentPeriodTypeStore.removeAll();

        getReferences('INSTALLMENT_PERIOD_TYPE',function(data){

            installmentPeriodTypeStore.add(data.root);
        });
        //COLLATERAL_TYPE

        var collateralTypeStore=Ext.getStore('CollateralTypeStore');
        collateralTypeStore.removeAll();

        getReferences('COLLATERAL_TYPE',function(data){

            collateralTypeStore.add(data.root);
        });

        //ContraAccount

        Ext.Ajax.request({
             url: '../Handlers/Finance/Maintenance/AccountHeadEntryHandler.ashx',
            params: {
                method:'GetContraAccount',
                offCode:getOfficeCode(),accountDesc:''
            },
            success: function(response){

                var data=Ext.decode(response.responseText);
                var contraAccountStore =Ext.getStore('ContraAccountStore');
                contraAccountStore.removeAll();
                contraAccountStore.add(data.root);


            }
        });
        //LoanHeading

        Ext.Ajax.request({
             url: '../Handlers/Finance/Transaction/LoanTransaction/MeLoanProductHandler.ashx',
            params: {
                method:'GetMeLoanProduct',
                productName:''
            },
            success: function(response){

                var data=Ext.decode(response.responseText);
                var loanHeadingStore =Ext.getStore('LoanHeadingStore');
                loanHeadingStore.removeAll();
                loanHeadingStore.add(data.root);
                console.log(data);

            }
        });










    }

