    onTxtEstablishedDateBSBlur: function(component, e, eOpts) {
         var wMsg='Please, Wait....';

                var waitSave = watiMsg(wMsg);

                Ext.Ajax.request({
                    url: '../Handlers/Common/DateHandler.ashx',
                    params: {
                        method:'GetEngDate',nepDate: Ext.ComponentQuery.query('#txtEstablishedDateBS')[0].getValue()
                   },
                    success: function(response){
                        waitSave.hide();
                        var out=Ext.decode(response.responseText);
                        console.log(out);

                        if(out.success==="true")
                        {
                            var message=out.message;

                                Ext.ComponentQuery.query('#txtEstablishedDateAD')[0].setValue(message);

                        }
                        else
                        {
                            msg("FAILURE",out.message);
                        }
                    },
                    failure: function ( result, request ) {
                        waitSave.hide();
                        var out=Ext.decode(response.responseText);
                        msg("FAILURE",out.message);
                    }

                });


    },

