    onBtnSearchClick: function(button, e, eOpts) {
        var waitSave = watiMsg('Loading Data. Please wait ...');
        Ext.Ajax.request({
                    url: '../Handlers/Finance/Transaction/PublicSavingTransaction/PublicAccountCloseDetailHandler.ashx',
                    params: {
                        method:'GetPubAccCloseDetail',
                        OfficeCode:getOfficeCode(),
                        WithdrawalNo:null,
        				SavingAccountNo:Ext.ComponentQuery.query('#ddlSearchSavingAccountNo')[0].getValue(),
        				ClientName:Ext.ComponentQuery.query('#txtSearchMemberName')[0].getValue(),
        				FromDate:Ext.ComponentQuery.query('#txtAccountCloseDateFromBS')[0].getValue(),
        				ToDate:Ext.ComponentQuery.query('#txtAccpountopenDateToBS')[0].getValue()

                    },
                    success: function(response){
                        var obj = Ext.decode(response.responseText);
                        if(obj.success === "true")
                        {

                           var store=Ext.getStore('PublicAccountCloseDetailStore');
                            store.removeAll();
                            store.add(obj.root);

                        }
                        else
                        {
                            msg("FAILURE",obj.message);
                        }
                        waitSave.hide();
                    }
                });
    },

