    extend: 'Ext.window.Window',

    requires: [
        'Ext.form.Panel',
        'Ext.form.field.Text',
        'Ext.button.Button',
        'Ext.grid.Panel',
        'Ext.grid.View',
        'Ext.selection.RowModel',
        'Ext.grid.column.Column'
    ],

    height: 500,
    itemId: 'SelectLoanHeadingPopup',
    width: 700,
    autoScroll: true,
    layout: 'fit',
    title: 'Select Loan Product',
    maximizable: true,
    minimizable: true,
