Ext.define('MyApp.controller.AddressType', {
    extend: 'Ext.app.Controller',

    stores: [
        'AddrTyp'
    ],

    onGridpanelItemClick: function(tablepanel, record, item, index, e, options) {
        Ext.ComponentQuery.query('#txtAddrTypId')[0].setValue(record.data.AddrsTypeID);
        Ext.ComponentQuery.query('#txtAddrTypDesNp')[0].setValue(record.data.AddressTypeDescNp);
        Ext.ComponentQuery.query('#txtAddrTypDesEn')[0].setValue(record.data.AddressTypeDescEn);

        Ext.ComponentQuery.query('#btnSubmits')[0].setText('Update');

    },

    onBtnSubmitsClick: function(button, e, options) {
        console.log("fired !!!");


        var txtAddrTypId = button.up('panel').down('textfield[itemId=txtAddrTypId]');
        var txtAddrTypDesEn = button.up('panel').down('textfield[itemId=txtAddrTypDesEn]');
        var txtAddrTypDesNp = button.up('panel').down('textfield[itemId=txtAddrTypDesNp]');
        var chkStatus = button.up('panel').down('checkboxfield[itemId=chkAddrStatus]');


        var strAddrTyp = Ext.getStore('AddrTyp');

        var status = "";


        if(chkStatus.checked)
        status = "Y";
        else
        status = "N";



        var postData = {
            AddrsTypeID:txtAddrTypId.getValue(),
            AddressTypeDescEn:txtAddrTypDesEn.getValue(),
            AddressTypeDescNp:txtAddrTypDesNp.getValue(),
            Status:status
        } ;


        Ext.Ajax.request({
            url: '../Handlers/Common/AddressTypeHandler.ashx?method=SaveAddressType',
            params: {addType:JSON.stringify(postData)},
            success: function ( result, request ) {
                // var jsonData = Ext.JSON.decode(result.responseText);
                //console.log("res : " + result.responseText);
                //  console.log("jsonData1 : " + jsonData);
                // var resultMessage = jsonData.data.result;
                //   console.log("resultMessage1 : " + resultMessage);
                //this.fnAlertMsg(resultMessage, 'Success');
                //this.Msg('Successfully Saved.', 'Success');


                strAddrTyp.load();

                Ext.Msg.show({
                    title: 'Success',
                    msg: 'Successfully Saved.' ,
                    buttons: Ext.MessageBox.OK,
                    icon: Ext.MessageBox.INFO
                });

            },
            failure: function ( result, request ) {
                /* var jsonData = Ext.JSON.decode(result.responseText);

                console.log("jsonData2 : " + jsonData);
                var resultMessage = jsonData.data.result;
                console.log("resultMessage2 : " + resultMessage);
                this.fnAlertMsg(resultMessage, 'Error');*/
                this.fnAlertMsg('ERROR OCURRED !!!', 'Error');
            }

        });

        console.log("over");
    },

    init: function(application) {
        this.control({
            "#grdAddress": {
                itemclick: this.onGridpanelItemClick
            },
            "#btnSubmits": {
                click: this.onBtnSubmitsClick
            }
        });
    }

});
