Ext.define('MyApp.controller.BusinessCategories', {
    extend: 'Ext.app.Controller',

    stores: [
        'bcStrBusinessCat'
    ],

    onBtnBusCatSaveClick: function(button, e, options) {
        var txtBusCatId=Ext.ComponentQuery.query('#txtBusCatId')[0];
        var txtBusCatEng=Ext.ComponentQuery.query('#txtBusCatEngBc')[0];
        var txtBusCatNep=Ext.ComponentQuery.query('#txtBusCatNep')[0];
        var chkBusStatus=Ext.ComponentQuery.query('#chkBusCatStatus')[0];

        var status = "";


        if(status.checked)
        status = true;
        else
        status = false;


        var postData = {
            BusCatId:txtBusCatId.getValue(),
            BusCatEng:txtBusCatEngBc.getValue(),
            BusCatNep:txtBusCatNep.getValue(),        
            Status:status
        } ;



        Ext.Ajax.request({
            url: '../Handlers/Common/BusinessCategoriesHandler.ashx?method=SaveBusCategories',
            params: {bank:JSON.stringify(postData)},

            success: function ( result, request )
            {
                Ext.Msg.show({
                    title: 'Success',
                    msg: 'Successfully Saved.' ,
                    buttons: Ext.MessageBox.OK,
                    icon: Ext.MessageBox.INFO
                });

            },
            failure: function ( result, request ) {

                this.fnAlertMsg('ERROR OCURRED !!!', 'Error');
            }

        });

    },

    onGrdBusinessCategoriesItemClick: function(tablepanel, record, item, index, e, options) {
        Ext.ComponentQuery.query('#txtBusCatId')[0].setValue(record.data.BusCategoryId);
        Ext.ComponentQuery.query('#txtBusCatEng')[0].setValue(record.data.BusCategoryDescEng);
        Ext.ComponentQuery.query('#txtBusCatNep')[0].setValue(record.data.BusCategoryDescNep);
        Ext.ComponentQuery.query('#chkBusCatStatus')[0].setValue(record.data.Status);
    },

    init: function(application) {
        this.control({
            "#btnBusCatSave": {
                click: this.onBtnBusCatSaveClick
            },
            "#grdBusinessCategories": {
                itemclick: this.onGrdBusinessCategoriesItemClick
            }
        });
    }

});
