Ext.define('MyApp.controller.BusinessPersonnelTypes', {
    extend: 'Ext.app.Controller',

    stores: [
        'strBusPerTypes'
    ],

    onGrdBusPerTypesItemClick: function(tablepanel, record, item, index, e, options) {
        Ext.ComponentQuery.query('#txtBusCatId')[0].setValue(record.data.BusPerType);
        Ext.ComponentQuery.query('#txtBusCatEng')[0].setValue(record.data.BusPerTypeDescEng);
        Ext.ComponentQuery.query('#txtBusCatNep')[0].setValue(record.data.BusPerTypeNep);
        Ext.ComponentQuery.query('#chkBusCatStatus')[0].setValue(record.data.Status);

    },

    init: function(application) {
        this.control({
            "#grdBusPerTypes": {
                itemclick: this.onGrdBusPerTypesItemClick
            }
        });
    }

});
