Ext.define('MyApp.controller.Designation', {
    extend: 'Ext.app.Controller',

    stores: [
        'Designation'
    ],

    onGridpanelItemClick: function(tablepanel, record, item, index, e, options) {
        Ext.ComponentQuery.query('#txtDegID')[0].setValue(record.data.DegsId);
        Ext.ComponentQuery.query('#txtDegEn')[0].setValue(record.data.DegsEng);
        Ext.ComponentQuery.query('#txtDegNp')[0].setValue(record.data.DegsNp);
        Ext.ComponentQuery.query('#chkStatusDeg')[0].setValue(record.data.StatusDeg);


        Ext.ComponentQuery.query('#btnDegSubmit')[0].setText('Update');
    },

    init: function(application) {
        this.control({
            "#grdDesignation": {
                itemclick: this.onGridpanelItemClick
            }
        });
    }

});
