Ext.define('MyApp.controller.DocReqAssignment', {
    extend: 'Ext.app.Controller',

    stores: [
        'strDocReqAssignment'
    ],
    views: [
        'DocReqAssignment'
    ],

    onDocReqAssignmentAfterRender: function(component, eOpts) {
        var me=this;

        var offCode=Ext.get('offCode').dom.innerHTML;
        var grdDocReqAssignment=Ext.ComponentQuery.query('#grdDocReqAssignment')[0];
        var strDocReqAssignment=Ext.getStore('strDocReqAssignment');
        strDocReqAssignment.removeAll();
        strDocReqAssignment.loadData([],false);

        var watiMsg=waitMsg('Loading ...');

        Ext.Ajax.request
        ({

            url:'../Handlers/DocumentManagement/DocumentRequestHandler.ashx?method=GetAllDocReqAssignment',
            params:{REQ_OFFCODE:offCode},


            success:function(response){
                console.log(response.responseText);
                var obj =Ext.decode(response.responseText);
                var row = obj.root;

                strDocReqAssignment.removeAll();
                strDocReqAssignment.loadData([],false);
                //console.log("row-->",obj.root[0].REQUEST_TYPE);       

                grdDocReqAssignment.store.add(obj.root);
                watiMsg.hide();

            },

            failure:function(response)
            {
                msg('FAILURE',Ext.decode(response));

            }



        });
    },

    init: function(application) {
        this.control({
            "#DocReqAssignment": {
                afterrender: this.onDocReqAssignmentAfterRender
            }
        });
    }

});
