Ext.define('MyApp.controller.Holiday', {
    extend: 'Ext.app.Controller',

    stores: [
        'Holiday'
    ],

    onGridpanelItemClick: function(tablepanel, record, item, index, e, options) {
        Ext.ComponentQuery.query('#txtoffcodeHoliday')[0].setValue(record.data.Offcode);
        Ext.ComponentQuery.query('#txtTaxYrHoliday')[0].setValue(record.data.Taxyear);
        Ext.ComponentQuery.query('#datefldStartDateHoliday')[0].setValue(record.data.Stdate);
        Ext.ComponentQuery.query('#datefldEndDateHoliday')[0].setValue(record.data.Endate);
        Ext.ComponentQuery.query('#txtHolidayDescHoliday')[0].setValue(record.data.HolidayDesc);


        Ext.ComponentQuery.query('#btnHolidaySubmit')[0].setText('Update');
    },

    onBtnHolidaySubmitClick: function(button, e, options) {
        console.log("fired !!!");


        var txtoffcodeHoliday = button.up('panel').down('textfield[itemId=txtoffcodeHoliday]');
        var txtTaxYrHoliday = button.up('panel').down('textfield[itemId=txtTaxYrHoliday]');
        var datefldStartDateHoliday = button.up('panel').down('textfield[itemId=datefldStartDateHoliday]');
        var datefldEndDateHoliday = button.up('panel').down('textfield[itemId=datefldEndDateHoliday]');
        var txtHolidayDescHoliday = button.up('panel').down('textfield[itemId=txtHolidayDescHoliday]');


        var strHoliday = Ext.getStore('Holiday');




        var postData = {
            Offcode:txtoffcodeHoliday.getValue(),
            Taxyear:txtTaxYrHoliday.getValue(),
            Stdate:datefldStartDateHoliday.getValue(),
            Endate:datefldStartDateHoliday.getValue(),
            HolidayDesc:txtHolidayDescHoliday.getValue()
        } ;


        Ext.Ajax.request({
            url: '../Handlers/Common/HolidayHandler.ashx?method=SaveHoliday',
            params: {objholiday:JSON.stringify(postData)},
            success: function ( result, request ) {
                // var jsonData = Ext.JSON.decode(result.responseText);
                //console.log("res : " + result.responseText);
                //  console.log("jsonData1 : " + jsonData);
                // var resultMessage = jsonData.data.result;
                //   console.log("resultMessage1 : " + resultMessage);
                //this.fnAlertMsg(resultMessage, 'Success');
                //this.Msg('Successfully Saved.', 'Success');


                strHoliday.load();

                Ext.Msg.show({
                    title: 'Success',
                    msg: 'Successfully Saved.' ,
                    buttons: Ext.MessageBox.OK,
                    icon: Ext.MessageBox.INFO
                });

            },
            failure: function ( result, request ) {
                /* var jsonData = Ext.JSON.decode(result.responseText);

                console.log("jsonData2 : " + jsonData);
                var resultMessage = jsonData.data.result;
                console.log("resultMessage2 : " + resultMessage);
                this.fnAlertMsg(resultMessage, 'Error');*/
                this.fnAlertMsg('ERROR OCURRED !!!', 'Error');
            }

        });

        console.log("over");
    },

    init: function(application) {
        this.control({
            "#grdHoliday": {
                itemclick: this.onGridpanelItemClick
            },
            "#btnHolidaySubmit": {
                click: this.onBtnHolidaySubmitClick
            }
        });
    }

});
