Ext.define('MyApp.controller.ManagementAdjustmentReturn', {
    extend: 'Ext.app.Controller',

    stores: [
        'MARSuspendedReturnsStore'
    ],

    onFrmManagementAdjustmentReturnAfterRender: function(component, eOpts) {
        var strMARSuspRet = Ext.getStore('MARSuspendedReturnsStore');
        strMARSuspRet.removeAll();
        //strVT.load();


        Ext.Ajax.request({
            url: '../Handlers/VAT/SuspendedReturnsHandler.ashx',
            params: {method:'GetSuspendedReturns',status:null },
            success: function(response){
                var JSONResponse=Ext.decode(response.responseText);
                var visitSelection=JSONResponse.root;
                if(JSONResponse.success=='true')
                {  
                    strMARSuspRet.add(JSONResponse.root);
                }

            }
        });
    },

    init: function(application) {
        this.control({
            "#frmManagementAdjustmentReturn": {
                afterrender: this.onFrmManagementAdjustmentReturnAfterRender
            }
        });
    }

});
