Ext.define('MyApp.controller.PANSearchCriteria', {
    extend: 'Ext.app.Controller',

    stores: [
        'PanSrchOfficeStore',
        'PanSrchDistrictStore',
        'PANSearchBusCategoryStore',
        'PANSearchBusSubCategoryStore',
        'PANSearchGridStore',
        'PANSearchOutstandingAsaDate',
        'Excise_Reg_AddGodown_Contact',
        'PANSearchRegDetails'
    ],
    views: [
        'PanSearchPopUpWin'
    ],

    onPanelAfterRender: function(abstractcomponent, options) {

        var officeCode=Ext.get('offCode').dom.innerHTML;
        var str=Ext.getStore('PanSrchOfficeStore');

        str.removeAll();

        /*str.load({
        params:{categoryID:BusCatID}
        });*/


        Ext.Ajax.request
        ({

            url:'../Handlers/Common/OfficeHandler.ashx?method=GetOfficeByParentType',
            params:{parentID:officeCode},

            success:function(response){
                console.log(response.responseText);
                var obj =Ext.decode(response.responseText);
                var row = obj.root;

                str.add(row);

                // str.loadData(row,true);

            },
            failure:function()
            {
                msg('FAILURE',Ext.decode(response));

            }



        });


    },

    init: function(application) {
        this.control({
            "#PanSearchCriteriaForm": {
                afterrender: this.onPanelAfterRender
            }
        });
    }

});
