Ext.define('MyApp.controller.SuspendedReasonErrors', {
    extend: 'Ext.app.Controller',

    models: [
        'SuspendedReason'
    ],
    stores: [
        'SuspendedReasonStore'
    ],

    onPopupWindowAfterRender: function(component, eOpts) {
        var objParam = Ext.ComponentQuery.query('#SuspendedReasonErrors')[0];

        var param = objParam.extraParam.params;



        Ext.Ajax.request({
            url: '../Handlers/VAT/LookUps/SuspendedReasonHandler.ashx',
            params: {method:'GetSuspendedReasons',suspendedCode:param.errorCode },
            success: function(response){
                var JSONResponse=Ext.decode(response.responseText);
                var visitSelection=JSONResponse.root;
                if(JSONResponse.success=='true')
                {  
                    var strSuspendedReasons=Ext.getStore('SuspendedReasonStore');
                    strSuspendedReasons.removeAll();

                    strSuspendedReasons.add(JSONResponse.root);
                }

            }
        });
    },

    init: function(application) {
        this.control({
            "#SuspendedReasonErrors": {
                afterrender: this.onPopupWindowAfterRender
            }
        });
    }

});
