Ext.define('MyApp.controller.TDSListWithholdee', {
    extend: 'Ext.app.Controller',

    models: [
        'TDSWithLogList'
    ],
    stores: [
        'TDSWithLogList',
        'TDSRegStatus'
    ],

    onGrdLWTDSListItemClick: function(dataview, record, item, index, e, eOpts) {
        var Pass = Ext.ComponentQuery.query("#hdnTDSLWPass")[0];
        var CreatedBy = Ext.ComponentQuery.query("#hdnTDSLWCreatedBy")[0];
        var PinCode = Ext.ComponentQuery.query("#hdnTDSLWPinCode")[0];

        var store = Ext.getStore('TDSWithLogList');
        var data = store.getAt(index);

        Pass.setValue(data.get('Pass'));
        CreatedBy.setValue(data.get('CreatedBy'));
        PinCode.setValue(data.get('PinCode'));
    },

    ShowWithList: function() {
        Ext.getCmp('cntOMTDS').removeAll();

        var pnlDynamic = Ext.create('MyApp.view.TDSListWithholdee');

        var pnlToRender = Ext.ComponentQuery.query("#cntOMTDS")[0];

        pnlToRender.add(pnlDynamic);

        var withListStore = Ext.getStore('TDSWithLogList');
        withListStore.load();
    },

    init: function(application) {
        this.control({
            "#grdLWTDSList": {
                itemclick: this.onGrdLWTDSListItemClick
            }
        });
    }

});
