Ext.define('MyApp.controller.TDSRequestKey', {
    extend: 'Ext.app.Controller',

    views: [
        'TDSRequestKey'
    ],

    ShowReqKey: function(pan, mail) {
        Ext.getCmp('cntOMTDS').removeAll();

        var pnlDynamic = Ext.create('MyApp.view.TDSRequestKey');

        var pnlToRender = Ext.ComponentQuery.query("#cntOMTDS")[0];
        pnlToRender.add(pnlDynamic);

        var txtPan = Ext.ComponentQuery.query("#txtRKTDSPan")[0];
        var txtMail = Ext.ComponentQuery.query("#txtRKTDSEmailAdd")[0];

        txtPan.setValue(pan);
        txtMail.setValue(mail);
    }

});
