Ext.define('MyApp.controller.TaxPayerType', {
    extend: 'Ext.app.Controller',

    stores: [
        'taxptyp'
    ],

    onGridpanelItemClick: function(dataview, record, item, index, e, eOpts) {
        Ext.ComponentQuery.query('#txtTaxPCat')[0].setValue(record.data.TaxPayerCat);
        Ext.ComponentQuery.query('#txtTaxPTyp')[0].setValue(record.data.TaxPayerTyp);
        Ext.ComponentQuery.query('#txtDescNpTaxPTyp')[0].setValue(record.data.DescNp);
        Ext.ComponentQuery.query('#txtDescEnTaxPTyp')[0].setValue(record.data.DescEn);

        Ext.ComponentQuery.query('#btnSubmitTaxPayerType')[0].setText('Update');
    },

    onBtnSubmitTaxPayerTypeClick: function(button, e, eOpts) {
        console.log("fired !!!");


        var txtTaxPCat = button.up('panel').down('textfield[itemId=txtTaxPCat]');
        var txtTaxPTyp = button.up('panel').down('textfield[itemId=txtTaxPTyp]');
        var txtDescNpTaxPTyp = button.up('panel').down('textfield[itemId=txtDescNpTaxPTyp]');
        var txtDescEnTaxPTyp = button.up('panel').down('textfield[itemId=txtDescEnTaxPTyp]');


        var strTaxPTyp = Ext.getStore('taxptyp');





        var postData = {
            TaxPayerCat:txtTaxPCat.getValue(),
            TaxPayerTyp:txtTaxPTyp.getValue(),
            DescNp:txtDescNpTaxPTyp.getValue(),
            DescEn:txtDescEnTaxPTyp.getValue()
        } ;


        Ext.Ajax.request({
            url: '../Handlers/Common/TaxPayerTypeHandler.ashx?method=SaveTaxPayerType',
            params: {taxPType:JSON.stringify(postData)},
            success: function ( result, request ) {
                // var jsonData = Ext.JSON.decode(result.responseText);
                //console.log("res : " + result.responseText);
                //  console.log("jsonData1 : " + jsonData);
                // var resultMessage = jsonData.data.result;
                //   console.log("resultMessage1 : " + resultMessage);
                //this.fnAlertMsg(resultMessage, 'Success');
                //this.Msg('Successfully Saved.', 'Success');


                strTaxPTyp.load();

                Ext.Msg.show({
                    title: 'Success',
                    msg: 'Successfully Saved.' ,
                    buttons: Ext.MessageBox.OK,
                    icon: Ext.MessageBox.INFO
                });

            },
            failure: function ( result, request ) {
                /* var jsonData = Ext.JSON.decode(result.responseText);

                console.log("jsonData2 : " + jsonData);
                var resultMessage = jsonData.data.result;
                console.log("resultMessage2 : " + resultMessage);
                this.fnAlertMsg(resultMessage, 'Error');*/
                this.fnAlertMsg('ERROR OCURRED !!!', 'Error');
            }

        });

        console.log("over");
    },

    init: function(application) {
        this.control({
            "#grdTaxPayerType": {
                itemclick: this.onGridpanelItemClick
            },
            "#btnSubmitTaxPayerType": {
                click: this.onBtnSubmitTaxPayerTypeClick
            }
        });
    }

});
