    onTxtStartDateBlur: function(field, eOpts) {
        return validateFutureDate(field.getValue(),"N",function(){field.focus();});
    },

    onButtonClick: function(button, e, eOpts) {
        var officeCode=Ext.ComponentQuery.query('#txtOfficeCode')[0].getValue();
        var stDate=Ext.ComponentQuery.query('#txtStartDate')[0].getValue();
        var endDate=Ext.ComponentQuery.query('#txtEndDate')[0].getValue();


        var count=0;
        var errorMsg = "";

        if(officeCode === "" || officeCode === null)
        {

            count++;
            errorMsg = errorMsg +'<br/>'+ count + ") कृपया Office Code भर्नुहोस.!!!";

        }

        if(stDate === "")
        {

            count++;
            errorMsg = errorMsg +'<br/>'+ count + ")कृपया Start Date  भर्नुहोस.!!!";

        }

        if(endDate ==="")
        {

            count++;
            errorMsg = errorMsg +'<br/>'+ count + ") कृपया End Date भर्नुहोस.!!! ";

        }

        if(count >0)
        {
            msg("WARNING",errorMsg);
            return false;
        }


        var obj={P_OFFCODE:officeCode,            
            P_ST_DATE:stDate,
            P_END_DATE:endDate  

        };
        var url="../Reporting/Vat/ReportHandlers/VAT_LIST_REPORT/BankReceiptPaymentHandler.ashx";
        var winOption="width=730,height=345,left=100,top=100,resizable=yes,scrollbars=yes";
        OpenWindowWithPost(url,winOption,"NewFile",obj);
    }

