Ext.define('MyApp.view.PanIssue', {
    extend: 'Ext.panel.Panel',

    frame: true,
    padding: 10,
    width: 600,
    title: 'Pan Issue',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'form',
                    frame: true,
                    itemId: 'frmPanIssue',
                    width: 346,
                    layout: {
                        type: 'auto'
                    },
                    title: 'Pan Issue',
                    items: [
                        {
                            xtype: 'container',
                            minHeight: 201,
                            padding: 10,
                            width: 332,
                            items: [
                                {
                                    xtype: 'textfield',
                                    itemId: 'PI_txtSubNo',
                                    margin: '0 0 10 0',
                                    width: 306,
                                    fieldLabel: 'Submission No.',
                                    allowBlank: false,
                                    enableKeyEvents: true,
                                    maskRe: /[0-9]/,
                                    listeners: {
                                        blur: {
                                            fn: me.onPI_txtSubNoBlur,
                                            scope: me
                                        }
                                    }
                                },
                                {
                                    xtype: 'combobox',
                                    hidden: true,
                                    itemId: 'PI_cmbFillingPeriod',
                                    margin: '0 0 10 0',
                                    width: 307,
                                    fieldLabel: 'Filling Period',
                                    emptyText: '---SELECT---',
                                    editable: false,
                                    displayField: 'FilPeriodDesc',
                                    queryMode: 'local',
                                    store: 'strPI_fillingPeriod',
                                    valueField: 'FilPeriod'
                                },
                                {
                                    xtype: 'textfield',
                                    itemId: 'PI_txtSeries',
                                    margin: '0 0 10 0',
                                    width: 307,
                                    fieldLabel: 'Series ',
                                    allowBlank: false,
                                    enableKeyEvents: true,
                                    maskRe: /[0-9]/
                                },
                                {
                                    xtype: 'textfield',
                                    itemId: 'PI_txtName',
                                    margin: '0 0 10 0',
                                    width: 307,
                                    fieldLabel: 'Name',
                                    readOnly: true
                                },
                                {
                                    xtype: 'textfield',
                                    itemId: 'PI_txtTradName',
                                    margin: '0 0 10 0',
                                    width: 307,
                                    fieldLabel: 'Trad Name',
                                    readOnly: true
                                },
                                {
                                    xtype: 'textfield',
                                    itemId: 'IP_txtPan',
                                    margin: '0 0 10 0',
                                    width: 307,
                                    fieldLabel: 'PAN',
                                    readOnly: true
                                },
                                {
                                    xtype: 'container',
                                    itemId: 'ctnButtons',
                                    style: 'text-align:right;',
                                    width: 310,
                                    items: [
                                        {
                                            xtype: 'button',
                                            height: 22,
                                            itemId: 'PI_bntPanAllocate',
                                            margin: '0 10',
                                            width: 80,
                                            text: ' Allocate PAN'
                                        },
                                        {
                                            xtype: 'button',
                                            itemId: 'PI_btnCancel',
                                            width: 80,
                                            text: 'Cancel'
                                        }
                                    ]
                                }
                            ]
                        }
                    ]
                }
            ]
        });

        me.callParent(arguments);
    },

    onPI_txtSubNoBlur: function(component, e, eOpts) {

        var me=this;
        var subNo=Ext.ComponentQuery.query('#PI_txtSubNo')[0];
        var pan=Ext.ComponentQuery.query('#IP_txtPan')[0];
        var series=Ext.ComponentQuery.query('#PI_txtSeries')[0];
        var fp=Ext.ComponentQuery.query('#PI_cmbFillingPeriod')[0];
        var fillingPeriod='';
        var accttype='';

        //var Form=Ext.ComponentQuery.query('#frmPanIssue')[0].getForm();

        if(!(subNo.getValue()))
        {
            msg("WARNING","Please enter the submission number");  
            subNo.focus();
            return;
        }
        else if(!(series.getValue()))
        {
            msg("WARNING","Please enter the series");  
            series.focus();
            return;
        }

        if(!fp.isHidden() && fp.getValue()!==null)
        {
            fillingPeriod=fp.getValue();
            accttype='00';
        }

        Ext.Ajax.request({
            url:"../Handlers/Registration/PanIssueHandler.ashx?method=UpdateSubNoForPanIssue",
            params:{SubNo:subNo.getValue(),
                Pan:pan.getValue(),
                fillingPeriod:fillingPeriod,
                acctype:accttype
            },
            success: function (result,request ){

                var obj = Ext.decode(result.responseText);
                if(obj.success === "true")
                {
                    // me.Clear();
                    Ext.ComponentQuery.query('#PI_bntPanAllocate')[0].disable();

                }

                msg(obj.success === "true" ?"SUCCESS":"FAILURE",obj.message);
                // Ext.ComponentQuery.query('#PI_txtSubNo')[0].focus();

                if(obj.success == "false") return;

            },

            failure: function ( result, request ){

                msg("FAILURE","Error in Fetching Data !!!");
                return;
            }


        });

    }

});