Ext.define('MyApp.view.ReviewAppealAAWindow', {
    extend: 'Ext.window.Window',

    height: 250,
    id: 'ReviewAppealAAWindow',
    itemId: 'ReviewAppealAAWindow',
    width: 520,
    autoScroll: true,
    title: 'कर निर्धरण आदेश नं. सूचि',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'gridpanel',
                    itemId: 'reviewAppealGrdAA',
                    width: 500,
                    autoScroll: true,
                    title: '',
                    store: 'ReviewAppealAA',
                    columns: [
                        {
                            xtype: 'rownumberer',
                            width: 30,
                            dataIndex: 'SeqNo',
                            text: 'क्र.सं.'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 120,
                            dataIndex: 'AssessmentNo',
                            text: ' कर निर्धारण  नं.'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 120,
                            dataIndex: 'AssessmentDate',
                            text: 'कर निर्धरण मिति'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 125,
                            dataIndex: 'AdditionalTax',
                            text: 'थप कर'
                        },
                        {
                            xtype: 'gridcolumn',
                            hidden: true,
                            width: 125,
                            dataIndex: 'SpecialCharge',
                            text: 'विषेश शुल्क'
                        },
                        {
                            xtype: 'gridcolumn',
                            hidden: true,
                            width: 125,
                            dataIndex: 'SpecialChargeSa',
                            text: 'विषेश शुल्क SA'
                        },
                        {
                            xtype: 'gridcolumn',
                            text: ' शुल्क'
                        }
                    ],
                    listeners: {
                        itemdblclick: {
                            fn: me.onReviewAppealGrdAAItemDblClick,
                            scope: me
                        },
                        afterrender: {
                            fn: me.onReviewAppealGrdAAAfterRender,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.callParent(arguments);
    },

    onReviewAppealGrdAAItemDblClick: function(dataview, record, item, index, e, eOpts) {

        var win=Ext.getCmp('ReviewAppealAAWindow');
        //console.log(win);

        var assessmentNo=win.assessmentNo;

        var grd=Ext.ComponentQuery.query('#reviewAppealGrdAA')[0];
        var selection=grd.getSelectionModel().getSelection()[0];
        var selectedIndx= grd.getStore().indexOf(selection);

        var storeAA=grd.getStore();

        assessmentNo.setValue(storeAA.getAt(selectedIndx).data.AssessmentNo);

        win.close();
    },

    onReviewAppealGrdAAAfterRender: function(component, eOpts) {
        var me = this;
        var grid = Ext.ComponentQuery.query('#reviewAppealGrdAA')[0];

        grid.columns[6].renderer = function(value, metaData, record, rowIndex, colIndex, store, view) {
            //var id = Ext.id();

            var row = grid.getStore().getAt(rowIndex);
            var sCharge = row.data.SpecialCharge;
            var sChargeSa = row.data.SpecialChargeSa;
            var totalSCharge = sCharge + sChargeSa;


            return totalSCharge;


        };

    }

});