Ext.define('MyApp.view.ReviewAppealCPWindow', {
    extend: 'Ext.window.Window',

    height: 250,
    id: 'ReviewAppealCPWindow',
    itemId: 'ReviewAppealCPWindow',
    width: 420,
    autoScroll: true,
    title: 'कर निर्धरण आदेश नं. सूचि',

    initComponent: function() {
        var me = this;

        Ext.applyIf(me, {
            items: [
                {
                    xtype: 'gridpanel',
                    itemId: 'reviewAppealGrdCP',
                    width: 400,
                    autoScroll: true,
                    title: '',
                    store: 'ReviewAppealCP',
                    columns: [
                        {
                            xtype: 'rownumberer',
                            width: 30,
                            dataIndex: 'SeqNo',
                            text: 'क्र.सं.'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 120,
                            dataIndex: 'ChargeNo',
                            text: 'शुल्क नं.'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 120,
                            dataIndex: 'ChargeDate',
                            text: 'शुल्क मिति'
                        },
                        {
                            xtype: 'gridcolumn',
                            width: 125,
                            dataIndex: 'TotalAmount',
                            text: 'शुल्क रकम'
                        }
                    ],
                    listeners: {
                        itemdblclick: {
                            fn: me.onReviewAppealGrdCPItemDblClick,
                            scope: me
                        }
                    }
                }
            ]
        });

        me.callParent(arguments);
    },

    onReviewAppealGrdCPItemDblClick: function(dataview, record, item, index, e, eOpts) {

        var win=Ext.getCmp('ReviewAppealCPWindow');
        //console.log(win);

        var chargeNo=win.chargeNo;

        var grd=Ext.ComponentQuery.query('#reviewAppealGrdCP')[0];
        var selection=grd.getSelectionModel().getSelection()[0];
        var selectedIndx= grd.getStore().indexOf(selection);

        var storeCP=grd.getStore();

        chargeNo.setValue(storeCP.getAt(selectedIndx).data.ChargeNo);

        win.close();
    }

});