Ext.define('MyApp.controller.PersonList', {
    extend: 'Ext.app.Controller',

    LoadCompanies: function(companyID, fromDate, toDate) {
        var companies=Ext.getStore('CompanyStore');
        companies.removeAll();


        var waitMsg=waitMsg('Loading ...');

        Ext.Ajax.request
        ({

            url:'../Handlers/Common/Company/CompanyHandler.ashx?method=GetCompaniesForIPO',
            params:{companyId:companyID,fromDate:fromDate,toDate:toDate},


            success:function(response){
                console.log(response.responseText);
                var obj =Ext.decode(response.responseText);
                var row = obj.root;



                companies.store.add(obj.root);
                waitMsg.hide();

            },

            failure:function(response)
            {
                waitMsg.hide();
                msg('FAILURE',Ext.decode(response));

            }



        });
    }

});
